package secuencia_control;

public class Ej5 {

	public static void tablas_multiplicar() {
		
		for(int i=1;i<=10;i++) {
			
			for(int j=0;j<=10;j++) {
				
				System.out.println(i+"x"+j+" = "+i*j);
				
			}
			
			System.out.println("");
			
		}
		
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Ej5.tablas_multiplicar();
		
	}

}
